mapper.update:
UPDATE house
SET is_deleted = 1
WHERE id = #{id}

mapper.select:
SELECT *
FROM house
WHERE is_deleted = 0

    AND (title LIKE CONCAT('%', #{keyword}, '%') OR description LIKE CONCAT('%', #{keyword}, '%'))


    AND house_type = #{type}


    AND area >= #{minArea}


    AND area<= #{maxArea}


    AND price >= #{minPrice}


    AND price<= #{maxPrice}


    AND address LIKE CONCAT('%', #{address}, '%')
